import * as React from "react";
import { Grid } from "@mui/material";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { Box } from "@mui/system";
import SellIcon from "@mui/icons-material/Sell";
import { makeStyles } from "@mui/styles";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import RemoveCircleIcon from "@mui/icons-material/RemoveCircle";
import { Badge } from "@mui/material";
import { CartState } from "../context/Context";

const useStyles = makeStyles({
  root: {
    maxWidth: 310,
    transition: "transform 0.15s ease-in-out",
  },
  cardHovered: {
    transform: "scale3d(1.05, 1.05, 1)",
  },
});

export default function () {
  const [cardData, setCardData] = React.useState();
  const classes = useStyles();
  const [selectedItemIndex, setSelectedItemIndex] = React.useState(-1);

  React.useEffect(() => {
    const fetchCardData = async () => {
      await fetch("https://fakestoreapi.com/products")
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setCardData(data);
        });
    };
    fetchCardData();
  }, []);

  const {
    state: { products },
  } = CartState();

  console.log(state);

  return (
    <Grid
      spacing={2}
      container
      sx={{ marginTop: 5, marginBottom: 5, marginLeft: 4 }}
    >
      {cardData?.map((cardItem) => (
        <Grid item spacing={2} xs={2} sx={{ minWidth: 200 }}>
          <Card sx={{ width: "100%", height: 450 }} elevation={5}>
            <CardMedia
              component="img"
              height="230"
              width="3"
              image={cardItem.image}
              alt="green iguana"
            />
            <CardContent>
              <Grid sx={{ height: 40 }}>
                <Typography
                  gutterBottom
                  variant="h5"
                  component="div"
                  style={{
                    fontSize: 15,
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                    display: "-webkit-box",
                    WebkitLineClamp: "2",
                    WebkitBoxOrient: "vertical",
                  }}
                >
                  {cardItem.title}
                </Typography>
              </Grid>

              <Grid sx={{ height: 40 }}>
                <Typography
                  variant="body2"
                  color="text.secondary"
                  style={{
                    fontSize: 15,
                    marginBottom: 12,
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                    width: "11rem",
                    display: "-webkit-box",
                    WebkitLineClamp: "2",
                    WebkitBoxOrient: "vertical",
                  }}
                >
                  {cardItem.description}
                </Typography>
              </Grid>

              <Grid container spacing={1}>
                <Grid item>
                  <Typography variant="body1" style={{ color: "#ff4d94" }}>
                    {cardItem.price}
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography variant="body1" style={{ color: "#005c99" }}>
                    {cardItem.price}
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
            <CardActions>
              <Button
                variant="contained"
                style={{ backgroundColor: "#86b300" }}
                onClick={() => {}}
              >
                Add Cart
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
