import * as React from "react";
import { Grid } from "@mui/material";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { Box } from "@mui/system";
import SellIcon from "@mui/icons-material/Sell";
import { makeStyles } from "@mui/styles";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import RemoveCircleIcon from "@mui/icons-material/RemoveCircle";
import { Badge } from "@mui/material";

const useStyles = makeStyles({
  root: {
    maxWidth: 310,
    transition: "transform 0.15s ease-in-out",
  },
  cardHovered: {
    transform: "scale3d(1.05, 1.05, 1)",
  },
});

export default function () {
  const [cardData, setCardData] = React.useState();
  const classes = useStyles();
  const [selectedItemIndex, setSelectedItemIndex] = React.useState(-1);

  const [state, setState] = React.useState({
    raised: false,
    shadow: 1,
  });

  React.useEffect(() => {
    const fetchCardData = async () => {
      await fetch("https://fakestoreapi.com/products")
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setCardData(data);
        });
    };
    fetchCardData();
  }, []);

  return (
    <Grid
      spacing={2}
      container
      sx={{ marginTop: 5, marginBottom: 5, marginLeft: 4 }}
    >
      {cardData?.map((cardItem) => (
        <Grid item spacing={2} xs={2} sx={{ minWidth: 400 }}>
          <Card sx={{ width: "100%" }}>
            <CardMedia
              component="img"
              height="230"
              width="5"
              image={cardItem.image}
              alt="green iguana"
            />
            <CardContent>
              <Typography
                gutterBottom
                variant="h5"
                component="div"
                style={{ fontSize: 15 }}
              >
                {cardItem.title}
              </Typography>
              <Typography
                variant="body2"
                color="text.secondary"
                style={{
                  fontSize: 15,
                  marginBottom: 12,
                  overflow: "hidden",
                  textOverflow: "ellipsis",
                  width: "11rem",
                }}
              >
                {cardItem.description}
              </Typography>
              <Grid container spacing={1}>
                <Grid item>
                  <Typography variant="body1" style={{ color: "#ff4d94" }}>
                    {cardItem.price}
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography variant="body1" style={{ color: "#005c99" }}>
                    {cardItem.price}
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
            <CardActions>
              <Button
                variant="contained"
                style={{ backgroundColor: "#86b300" }}
                onClick={() => {}}
              >
                Add Cart
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
