import {
  Grid,
  MenuItem,
  TextField,
  Typography,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  Button,
} from "@mui/material";
import { Form, Formik, useFormik } from "formik";
import React, { useState } from "react";
import { FormTitle } from "../../../styles/Common.Styled";
import { validationSchema } from "../../../components/FormValidation/Validation";
import { Data, state } from "../../../mocks/orderMocks";
import Navbar from "../../../components/Navbar/Navbar";
import { Footer } from "../../../components/Footer/Footer";
import { postUserAddress } from "../../../sevices";
import ExistAddress from "./ExistAddress";

const AddAddress = () => {
  const [toggle, setToggle] = React.useState(false);
  const [userData, setUserData] = useState({});
  // const [record , setRecord] = React.useState([] as any)

  const formik = useFormik({
    initialValues: {
      name: "",
      mobileNo: "",
      address: "",
      city: "",
      state: "",
      country: "",
      zip: "",
      addressType: "",
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      alert(JSON.stringify(values, null));
      setUserData({ ...values });
    },
  });

  // const onClick =  () =>{

  //  const data = {...userData , id: new Date().getTime().toString()}
  //  setRecord([...record ,data])}

  // const submit = async () =>{
  //    const data = await postUserAddress(record)
  // }
  // console.log(record , "rec")
  console.log(userData, "ud");
  console.log(formik.values);

  return !toggle ? (
    <Grid container>
      <Grid item md={2}></Grid>
      <Grid item md={8}>
        <form onSubmit={formik.handleSubmit}>
          <Grid container>
            <Grid item marginTop={2}>
              <Typography
                variant="h5"
                sx={{ color: "#67757C", fontWeight: 600 }}
              >
                Add New Address
              </Typography>
            </Grid>
            <Grid container spacing={2}>
              <Grid item md={6} sm={12} xs={12}>
                <FormTitle>Name</FormTitle>
                <TextField
                  name="name"
                  fullWidth
                  placeholder="Name"
                  value={formik.values.name}
                  onChange={formik.handleChange}
                  error={formik.touched.name && Boolean(formik.errors.name)}
                  helperText={formik.touched.name && formik.errors.name}
                ></TextField>
              </Grid>
              <Grid item md={6} sm={12} xs={12}>
                <FormTitle>Mobile Number</FormTitle>
                <TextField
                  name="mobileNo"
                  fullWidth
                  placeholder="Mobile Number"
                  value={formik.values.mobileNo}
                  onChange={formik.handleChange}
                  error={
                    formik.touched.mobileNo && Boolean(formik.errors.mobileNo)
                  }
                  helperText={formik.touched.mobileNo && formik.errors.mobileNo}
                ></TextField>
              </Grid>
            </Grid>
            <Grid container spacing={2} marginTop={1.5}>
              <Grid item md={12} sm={12} xs={12}>
                <FormTitle>Address</FormTitle>
                <TextField
                  name="address"
                  fullWidth
                  placeholder="Address (Area and Street)"
                  value={formik.values.address}
                  onChange={formik.handleChange}
                  error={
                    formik.touched.address && Boolean(formik.errors.address)
                  }
                  helperText={formik.touched.address && formik.errors.address}
                ></TextField>
              </Grid>
            </Grid>
            <Grid container spacing={2} marginTop={0.5}>
              <Grid item md={6} sm={6} xs={12}>
                <FormTitle>City</FormTitle>
                <TextField
                  fullWidth
                  name="city"
                  value={formik.values.city}
                  onChange={formik.handleChange}
                  error={formik.touched.city && Boolean(formik.errors.city)}
                  helperText={formik.touched.city && formik.errors.city}
                ></TextField>
              </Grid>
              <Grid item md={6} sm={6} xs={12}>
                <FormTitle>State</FormTitle>
                <TextField
                  fullWidth
                  select
                  name="state"
                  value={formik.values.state}
                  onChange={formik.handleChange}
                  error={formik.touched.state && Boolean(formik.errors.state)}
                  helperText={formik.touched.state && formik.errors.state}
                >
                  {state.map((item) => (
                    <MenuItem value={item.value}>{item.label}</MenuItem>
                  ))}
                </TextField>
              </Grid>
            </Grid>
            <Grid container spacing={2} marginTop={0.5}>
              <Grid item md={6} sm={6} xs={12}>
                <FormTitle>Country</FormTitle>
                <TextField
                  fullWidth
                  name="country"
                  value={formik.values.country}
                  onChange={formik.handleChange}
                  error={
                    formik.touched.country && Boolean(formik.errors.country)
                  }
                  helperText={formik.touched.country && formik.errors.country}
                ></TextField>
              </Grid>
              <Grid item md={6} sm={6} xs={12}>
                <FormTitle>Zip</FormTitle>
                <TextField
                  fullWidth
                  name="zip"
                  value={formik.values.zip}
                  onChange={formik.handleChange}
                  error={formik.touched.zip && Boolean(formik.errors.zip)}
                  helperText={formik.touched.zip && formik.errors.zip}
                ></TextField>
              </Grid>
            </Grid>
            <Grid container spacing={2} marginTop={0.5}>
              <Grid item md={12} sm={12} xs={12} marginTop={1}>
                <FormTitle>Address Type</FormTitle>
                <FormControl>
                  <RadioGroup
                    row
                    name="addresstype"
                    value={formik.values.addressType}
                    onChange={formik.handleChange}
                  >
                    <FormControlLabel
                      control={<Radio />}
                      label="Home"
                      value="Home"
                    ></FormControlLabel>
                    <FormControlLabel
                      control={<Radio />}
                      label="Work"
                      value="Work"
                    ></FormControlLabel>
                    <FormControlLabel
                      control={<Radio />}
                      label="Other"
                      value="Other"
                    ></FormControlLabel>
                  </RadioGroup>
                </FormControl>
              </Grid>
              {/* <Grid item md={6} sm={6} xs={6}> */}

              <Button
                type="submit"
                variant="contained"
                // component={Link}
                // to = "/order/exist-address"
                // onClick={() => setToggle(true)

                sx={{ margin: 1, backgroundColor: "#84C225" }}
              >
                Add
              </Button>
              {/* </Grid> */}
              <Button
                variant="contained"
                sx={{ margin: 1, backgroundColor: "gray" }}
                onClick={() => setToggle(true)}
              >
                Cancel
              </Button>
            </Grid>
          </Grid>
        </form>
      </Grid>
      <Grid item sm={2}></Grid>
    </Grid>
  ) : (
    <ExistAddress />
  );
};
export default AddAddress;
