import { Divider, Grid, Typography } from "@mui/material";
import React, { useState } from "react";
import { Box } from "@mui/system";
import { Button } from "@mui/material";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import RemoveCircleIcon from "@mui/icons-material/RemoveCircle";
import ClearIcon from "@mui/icons-material/Clear";
import { CartState } from "../context/Context";

const Cart = () => {
  const { state, dispatch } = CartState();

  console.log(state.cart);

  return (
    <Box maxHeight={550} overflow="auto">
      {state.cart.map((item) => {
        return (
          <>
            <Grid container>
              <Grid item xs={3} marginTop={2}>
                <Box
                  component="img"
                  sx={{ width: 100, marginLeft: 2 }}
                  src={item.image}
                  alt="not Found"
                />
              </Grid>

              <Grid item xs={4} marginTop={2} marginLeft={3}>
                <Typography
                  style={{
                    fontFamily: "Poppins",
                    fontWeight: 400,
                    marginBottom: "7px",
                    fontSize: "15px",
                  }}
                >
                  {item.title}
                </Typography>

                <Grid container item sx={{ ml: 2, mt: 2 }} md={2}>
                  <Grid item>
                    <AddCircleIcon
                      fontSize="large"
                      sx={{
                        color: "#86b300",
                        cursor: "pointer",
                        alignItems: "center",
                      }}
                      onClick={() => {
                        item.qty &&
                          dispatch({
                            type: "CHANGE_CART_QTY",
                            payload: {
                              id: item.id,
                              qty: item.qty + 1,
                            },
                          });
                      }}
                    />
                  </Grid>

                  <Grid item>
                    <Box
                      sx={{ textAlign: "center", border: "1px solid" }}
                      pt={0.5}
                      pb={0.5}
                      pl={2.5}
                      pr={2.5}
                    >
                      {item.qty}
                    </Box>
                  </Grid>

                  <Grid item>
                    <RemoveCircleIcon
                      sx={{ color: "#86b300", cursor: "pointer" }}
                      fontSize="large"
                      onClick={() => {
                        item.qty &&
                          dispatch({
                            type: "CHANGE_CART_QTY",
                            payload: {
                              id: item.id,
                              qty: item.qty - 1,
                            },
                          });
                      }}
                    />
                  </Grid>
                </Grid>
              </Grid>
              <Grid item xs={4} marginTop={2}>
                <Typography
                  variant="body1"
                  component="span"
                  sx={{
                    color: "#ff4d94",
                    textDecoration: "line-through",
                    fontWeight: "bold",
                    padding: 1,
                  }}
                >
                  ₹{item.price.toFixed(2)}
                </Typography>

                <Typography
                  component="span"
                  sx={{ color: "#8bc34a", fontWeight: "bold", padding: 1 }}
                >
                  {/* ₹{(item.discountType === "%"
                              ? item.price -
                              (item.price * item.discountRate) / 100
                              : item.price - item.discountRate
                              ).toFixed(2)
                            } */}
                </Typography>
              </Grid>
            </Grid>
            <Divider />
          </>
        );
      })}
    </Box>
  );
};

export default Cart;
